import { useContext, useEffect, useState } from 'react'
import { CartContext } from '../contexts/CartContext'

const Carrello = () => {
  const { addToCart, productsInCart, setProductsInCart } = useContext(CartContext)

  const removeProduct = product => {
    const updateArray = productsInCart.filter(element => {
      return element._id !== product._id
    })
    console.log(updateArray, 'update')
    setProductsInCart(updateArray)
  }

  return (
    <div>
      <h1>Carrello</h1>
      {productsInCart && productsInCart.map(product => {
        return (
          <div key={product._id}>
            <p>{product.name}</p>
            <p>{product.boughtQuantity}</p>
            <button onClick={() => removeProduct(product)}>x</button>
          </div>
        )
      })}
    </div>
  )
}
export default Carrello
