import { createContext, useState, useEffect } from 'react'
import axios from 'axios'

export const CartContext = createContext()

const service = axios.create({
  baseURL: process.env.APP_API,
  withCredentials: true
})

const CartContextProvider = props => {
  let productsInLocalStorage

  // useEffect(() => {
  //   console.log('use effect')
  //   productsInLocalStorage = JSON.parse(localStorage.getItem('productsInCart'))
  // }, [productsInLocalStorage])
  // console.log(productsInLocalStorage, 'productsInLocalStorage')

  useEffect(() => {
    function fetchStorage () {
      productsInLocalStorage = JSON.parse(localStorage.getItem('productsInCart'))
    }
    fetchStorage()
  }, [productsInLocalStorage])

  // console.log(productsInLocalStorage, 'productsInLocalStorage')

  const [productsInCart, setProductsInCart] = useState([])

  const addToCart = async product => {
    setProductsInCart([...productsInCart, product])
    // await service.get(`${process.env.APP_API}/api/session-cart/${product._id}`)
    localStorage.setItem('productsInCart', JSON.stringify(productsInCart))
  }
  return (
    <CartContext.Provider value={{ productsInCart, addToCart }}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartContextProvider
