import { useContext, useState } from 'react'
import { UserContext } from '../../contexts/UserContext'

const Profilo = () => {
  const { user, updateUser } = useContext(UserContext)

  const [form, setForm] = useState({
    firstName: user.firstName,
    lastName: '',
    email: '',
    password: '',
  })

  const handleSubmit = async e => {
    e.preventDefault()
    console.log(user.firstName)
    console.log(form.firstName)
    try {
      updateUser(form)
    } catch (err) {
      console.log(err)
    }
  }


  return (
    <div>
      <h1>Profilo</h1>
      <form onSubmit={handleSubmit}>
        <input type='text' placeholder={user.firstName} onChange={(e) => setForm({ ...form, firstName: e.target.value })} />
        <input type='text' value={form.lastName} placeholder='* Cognome' onChange={(e) => setForm({ ...form, lastName: e.target.value })} />
        <input type='email' value={form.email} placeholder='* Email' onChange={(e) => setForm({ ...form, email: e.target.value })} />
        <input type='password' value={form.password} placeholder='*****' onChange={(e) => setForm({ ...form, password: e.target.value })} />
        <button>Aggiorna</button>
      </form>
    </div>
  )
}
export default Profilo
