import { useContext, useEffect, useState } from 'react'
import { CartContext } from '../contexts/CartContext'
import Link from 'next/link'

const Checkout = () => {
  const { updateCart, productsInCart, setProductsInCart } = useContext(CartContext)
  const [totalPrice, setTotalPrice] = useState(0)

  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    vatNumber: '',
    streetAddress: '',
    city: '',
    province: '',
    zipCode: '',
    telephone: '',
    email: '',
    additionalNotes: ''
  })

  const handleSubmit = async e => {
    e.preventDefault()
    try {
      console.log(form)
    } catch (err) {
      console.log(err)
    }
  }

  return (
    <div>
      <h1>Checkout</h1>
      <form onClick={handleSubmit}>
        <input type='text' value={form.firstName} placeholder='* Nome' required onChange={(e) => setForm({ firstName: e.target.value })} />
        <input type='text' value={form.lastName} placeholder='* Cognome' required onChange={(e) => setForm({ lastName: e.target.value })} />
        <button>Effettua ordine</button>
      </form>
    </div>
  )
}
export default Checkout
