import { createContext, useState } from 'react'
import axios from 'axios'

export const ProductContext = createContext()

const service = axios.create({
  baseURL: process.env.APP_API,
  withCredentials: true
})

const ProductContextProvider = props => {
  let [products, setProducts] = useState([])

  const getProducts = async () => {
    const response = await service.get(`${process.env.APP_API}/api/products`)
    setProducts(products = response.data)
  }

  const getSingleProduct = async id => {
    console.log(id, 'id from contet')
    if (id) {
      return
    }
    const product = await service.get(`${process.env.APP_API}/api/product/${id}`)
    console.log(product, 'product')
    return product
  }
  return (
    <ProductContext.Provider value={{ products, getProducts, getSingleProduct }}>
      {props.children}
    </ProductContext.Provider>
  )
}

export default ProductContextProvider
