import { useRouter } from "next/router";
import { useContext } from 'react'
import { CartContext } from '../../contexts/CartContext'
import { ProductContext } from '../../contexts/ProductContext'

const Product = ({ product }) => {
  const router = useRouter();
  const { id } = router.query; // Destructuring our router object
  const { addToCart, productsInCart } = useContext(CartContext)
  const { getSingleProduct } = useContext(ProductContext)

  getSingleProduct(id)
    return (
      <div>
        <h1>Product page</h1>
        {/* <p>{product.name}</p> */}
        <button onClick={() => addToCart(product)}>Add to cart</button>
      </div>
    )
  }

  // export async function getServerSideProps ({ params }) {
  //   // Fetch data from external API
  //   const res = await fetch(`${process.env.APP_API}/api/product/${params.id}`)
  //   const product = await res.json()

  //   // Pass data to the page via props
  //   return { props: { product } }
  // }

export default Product
