import { loadStripe } from '@stripe/stripe-js'
import { Elements } from '@stripe/react-stripe-js'
import CheckoutForm from '../components/CheckoutForm'
import { useContext, useEffect, useState } from 'react'
import { CartContext } from '../contexts/CartContext'
import axios from 'axios'
import Link from 'next/link'

// Make sure to call loadStripe outside of a component’s render to avoid
// recreating the Stripe object on every render.
// loadStripe is initialized with a fake API key.
// Sign in to see examples pre-filled with your key.
const promise = loadStripe(process.env.STRIPE_PK)

const service = axios.create({
  baseURL: process.env.APP_API,
  withCredentials: true
})

const Pagamento = () => {
  const { productsInCart, shippingInfo, totalPrice, calculateTotalPrice } = useContext(CartContext)

  // useEffect(async () => {
  //   await service.post('/create-order', {
  //     name: 'test'
  //   })
  //   calculateTotalPrice()

  // })

  useEffect(() => {
    // async function createOrder () {
    //   await service.post('/create-order', {
    //     name: 'test'
    //   })
    // }
    // createOrder()
    calculateTotalPrice()
  }, [])

  const createOrder = async (product) => {
    await service.post('/create-order', {
      name: 'test'
    })
  }
  return (
    <div>
      <h1>Pagamento: {totalPrice}</h1>
      <button onClick={() => createOrder()}>click</button>
      <Elements stripe={promise}>
        <CheckoutForm />
      </Elements>
    </div>
  )
}
export default Pagamento
