const checkIfAdmin = async () => {
  const router = useRouter()
  try {
    const res = await service.get('/admin/get-admin')
    if (res.data.admin) {
      return res.data.admin
    } else {
      router.push('/')
    }
  } catch (e) {
    console.log(e)
  }
}

export default checkIfAdmin
