import { useRouter } from "next/router";
import { useContext, useEffect, useState } from 'react'
import { CartContext } from '../../contexts/CartContext'
import { ProductContext } from '../../contexts/ProductContext'

const Product = () => {
  const router = useRouter();
  const { id } = router.query; // Destructuring our router object
  const { addToCart, productsInCart, test } = useContext(CartContext)
  const { getSingleProduct, singleProduct } = useContext(ProductContext)

  const [quantity, setQuantity] = useState(1)


 useEffect(() => {
    async function fetchProduct () {
      await getSingleProduct(id)
    }
    fetchProduct()
  }, [id])




    return (
      <div>
        <h1>Product page</h1>
        <p>{singleProduct && singleProduct.name}</p>
        <p>{quantity}</p>
        <button onClick={setQuantity(quantity + 1)}>Add</button>
        <button>Remove</button>
        <button onClick={() => addToCart(singleProduct, quantity)}>Add to cart</button>
      </div>
    )
  }


export default Product
