const AppProvider = ({ children }) => {
  const [galleryData, setGalleryData] = React.useState([])

  const handleGalleryData = galleryData => {
    setGalleryData(galleryData)
  }

  const contextProps = {
    galleryData,
    handleGalleryData
  }

  return (
    <AppContext.Provider value={contextProps}>
      {children}
    </AppContext.Provider>
  )
}
